mapper.update:
UPDATE orders
status = #{status}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status= #{payStatus}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, delivery_time = #{deliveryTime},
WHERE id = #{id}

mapper.select:
SELECT * FROM orders

    AND status = #{status}
    AND user_id = #{userId}
    AND order_time >= #{beginTime}
    AND order_time <= #{endTime}
    AND number = #{number}
    AND phone = #{phone}

ORDER BY order_time DESC

SELECT COUNT(amount) FROM orders

    AND status = #{status}
    AND order_time >= #{beginTime}
    AND order_time <= #{endTime}

SELECT COUNT(id) FROM orders

    AND status = #{status}
    AND order_time >= #{beginTime}
    AND order_time <= #{endTime}
